namespace ScriptTest;

public class OpenerTest : IOpener
{
    // 留空代表使用ACR提供的起手
    public List<Action<Slot>> Sequence { get; } = new();

    // 处理倒计时期间的行为
    public void InitCountDown(CountDownHandler countDownHandler)
    {
        //起手
        Wotou.Bard.Setting.BardSettings.Instance.Opener = 2;
        if (Wotou.Bard.BardRotationEntry.QT.GetQt("爆发药") && Wotou.Bard.Setting.BardSettings.Instance.UsePotionInOpener)
               countDownHandler.AddAction(1000, Spell.CreatePotion);
    }
}
